import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Brain, Clock, Trophy, Users, Globe, Gamepad2, Laptop, BookOpen, Film } from 'lucide-react';
import './QuizStart.css';

const QuizStart = ({ categories, onStartQuiz }) => {
  const [selectedDifficulty, setSelectedDifficulty] = useState('easy');
  const difficulties = [
    { value: 'easy', label: 'Easy', color: '#22c55e' },
    { value: 'medium', label: 'Medium', color: '#f59e0b' },
    { value: 'hard', label: 'Hard', color: '#ef4444' }
  ];
  const categoryIcons = {
    'General Knowledge': Brain,
    'Science': Brain,
    'History': Brain,
    'Geography': Globe,
    'Sports': Gamepad2,
    'Technology': Laptop,
    'Literature': BookOpen,
    'Movies': Film
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        type: 'spring',
        stiffness: 100
      }
    }
  };

  return (
    <motion.div
      className="quiz-start"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      <motion.div className="header" variants={itemVariants}>
        <h1 className="title">Quiz Challenge</h1>
        <p className="subtitle">Test your knowledge across various categories</p>
      </motion.div>

      <motion.div className="features" variants={itemVariants}>
        <div className="feature">
          <Clock className="feature-icon" />
          <span>Timed Questions</span>
        </div>
        <div className="feature">
          <Trophy className="feature-icon" />
          <span>Score Tracking</span>
        </div>
        <div className="feature">
          <Users className="feature-icon" />
          <span>Multiple Categories</span>
        </div>
      </motion.div>

      <motion.div className="difficulty-section" variants={itemVariants}>
        <h2>Choose Difficulty</h2>
        <div className="difficulty-buttons">
          {difficulties.map((difficulty, index) => (
            <motion.button
              key={difficulty.value}
              className={`difficulty-button ${selectedDifficulty === difficulty.value ? 'selected' : ''}`}
              onClick={() => setSelectedDifficulty(difficulty.value)}
              style={{ '--difficulty-color': difficulty.color }}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              {difficulty.label}
            </motion.button>
          ))}
        </div>
      </motion.div>

      <motion.div className="categories-section" variants={itemVariants}>
        <h2>Choose a Category</h2>
        <div className="categories-grid">
          {categories.map((category, index) => {
            const IconComponent = categoryIcons[category] || Brain;
            return (
              <motion.button
                key={category}
                className="category-card"
                onClick={() => onStartQuiz(category, selectedDifficulty)}
                whileHover={{ scale: 1.05, y: -5 }}
                whileTap={{ scale: 0.95 }}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 }}
              >
                <IconComponent className="category-icon" />
                <span className="category-name">{category}</span>
              </motion.button>
            );
          })}
        </div>
      </motion.div>

      <motion.div className="instructions" variants={itemVariants}>
        <h3>How it works:</h3>
        <ul>
          <li>Select a category to start the quiz</li>
          <li>Answer questions within the time limit</li>
          <li>Get instant feedback on your answers</li>
          <li>View your final score and performance</li>
        </ul>
      </motion.div>
    </motion.div>
  );
};

export default QuizStart;
