import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ArrowLeft, Clock, CheckCircle, XCircle } from 'lucide-react';
import Question from './Question';
import ProgressBar from './ProgressBar';
import Timer from './Timer';
import './Quiz.css';

const Quiz = ({ questions, category, onFinishQuiz, onBackToStart }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [timeSpent, setTimeSpent] = useState(0);
  const [showFeedback, setShowFeedback] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isAnswered, setIsAnswered] = useState(false);

  const currentQuestion = questions[currentQuestionIndex];
  const progress = ((currentQuestionIndex + 1) / questions.length) * 100;

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeSpent(prev => prev + 1);
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const handleAnswerSelect = (answerIndex) => {
    if (isAnswered) return;
    
    setSelectedAnswer(answerIndex);
    setIsAnswered(true);
    setShowFeedback(true);

    const isCorrect = answerIndex === currentQuestion.correctAnswer;
    const newAnswer = {
      questionIndex: currentQuestionIndex,
      selectedAnswer: answerIndex,
      correctAnswer: currentQuestion.correctAnswer,
      isCorrect,
      timeSpent: timeSpent
    };

    setUserAnswers(prev => [...prev, newAnswer]);

    // Auto-advance after showing feedback
    setTimeout(() => {
      nextQuestion();
    }, 2000);
  };

  const nextQuestion = () => {
    setShowFeedback(false);
    setSelectedAnswer(null);
    setIsAnswered(false);

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
    } else {
      finishQuiz();
    }
  };

  const finishQuiz = () => {
    const score = userAnswers.filter(answer => answer.isCorrect).length;
    onFinishQuiz(userAnswers, score, timeSpent);
  };

  const skipQuestion = () => {
    const newAnswer = {
      questionIndex: currentQuestionIndex,
      selectedAnswer: null,
      correctAnswer: currentQuestion.correctAnswer,
      isCorrect: false,
      timeSpent: timeSpent
    };

    setUserAnswers(prev => [...prev, newAnswer]);
    nextQuestion();
  };

  if (!currentQuestion) {
    return <div>Loading...</div>;
  }

  return (
    <motion.div
      className="quiz-container"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="quiz-header">
        <button className="back-button" onClick={onBackToStart}>
          <ArrowLeft className="back-icon" />
          Back to Categories
        </button>
        
        <div className="quiz-info">
          <h2 className="category-title">{category}</h2>
          <div className="quiz-stats">
            <span className="question-counter">
              Question {currentQuestionIndex + 1} of {questions.length}
            </span>
            <Timer timeSpent={timeSpent} />
          </div>
        </div>
      </div>

      <ProgressBar progress={progress} />

      <AnimatePresence mode="wait">
        <motion.div
          key={currentQuestionIndex}
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -50 }}
          transition={{ duration: 0.3 }}
          className="question-container"
        >
          <Question
            question={currentQuestion}
            onAnswerSelect={handleAnswerSelect}
            selectedAnswer={selectedAnswer}
            showFeedback={showFeedback}
            isAnswered={isAnswered}
          />
        </motion.div>
      </AnimatePresence>

      {showFeedback && (
        <motion.div
          className="feedback"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
        >
          {selectedAnswer === currentQuestion.correctAnswer ? (
            <div className="feedback-correct">
              <CheckCircle className="feedback-icon" />
              <span>Correct! Well done!</span>
            </div>
          ) : (
            <div className="feedback-incorrect">
              <XCircle className="feedback-icon" />
              <span>
                Incorrect. The correct answer is: {currentQuestion.options[currentQuestion.correctAnswer]}
              </span>
            </div>
          )}
        </motion.div>
      )}

      <div className="quiz-controls">
        {!isAnswered && (
          <button className="skip-button" onClick={skipQuestion}>
            Skip Question
          </button>
        )}
      </div>
    </motion.div>
  );
};

export default Quiz;
